mapper.select:
/* item-req.findListItemReq : 품목등록요청 목록 조회 */ SELECT REQ.TEN_ID /*시스템 아이디 */ , REQ.ITEM_REG_REQ_NO /*등록 요청 번호 */ , REQ.REQ_TYP_CCD /*요청 유형 */ , REQ.ITEM_CD /*품목 코드 */ , CASE WHEN 'en_US' = #{g.locale} THEN REQ.ITEM_NM_EN ELSE REQ.ITEM_NM END AS DISP_ITEM_NM , REQ.ITEM_NM /*품목 명 */ , REQ.ITEM_NM_EN , REQ.ITEM_SIMWORD , REQ.ITEM_SPEC /*규격 */ , REQ.UOM_CCD , REQ.ITEMCAT_LVL_1_CD /*분류1 코드 : 품목분류1 */ , REQ.ITEMCAT_LVL_2_CD /*분류2 코드 : 품목분류2 */ , REQ.ITEMCAT_LVL_3_CD /*분류3 코드 : 품목분류3 */ , REQ.ITEMCAT_LVL_4_CD /*분류4 코드 : 품목분류4 */ , REQ.ITEMCAT_LVL_5_CD , REQ.ATHG_UUID , REQ.IMG_ATHG_UUID , REQ.MFGR_NM /*제조사 명 */ , REQ.MDL_NO /*모델 번호 */ , REQ.RMK , REQ.REQR_ID /*요청자 아이디 */ , REQ.REQ_DTTM /*요청 일시 */ , REQ.APVR_ID , REQ.APVD_DTTM , REQ.ITEM_REG_REQ_RET_RSN , REQ.ITEM_REG_REQ_STS_CCD /*진행 상태 T:임시저장,P:요청,C:승인,B:반려 */ , REQ.STS , REQ.REGR_ID , REQ.REG_DTTM , REQ.MODR_ID , REQ.MOD_DTTM ,( SELECT CASE WHEN 'en_US' = #{g.locale} THEN CAT1.ITEMCAT_NM_EN ELSE CAT1.ITEMCAT_NM END FROM ITEMCAT CAT1 WHERE CAT1.TEN_ID = REQ.TEN_ID AND CAT1.ITEMCAT_CD = REQ.ITEMCAT_LVL_1_CD AND CAT1.STS != 'D' ) AS ITEMCAT_LVL_1_NM /* 품목분류1명 */ ,( SELECT CASE WHEN 'en_US' = #{g.locale} THEN CAT2.ITEMCAT_NM_EN ELSE CAT2.ITEMCAT_NM END FROM ITEMCAT CAT2 WHERE CAT2.TEN_ID = REQ.TEN_ID AND CAT2.ITEMCAT_CD = REQ.ITEMCAT_LVL_2_CD AND CAT2.STS != 'D' ) AS ITEMCAT_LVL_2_NM /* 품목분류2명 */ ,( SELECT CASE WHEN 'en_US' = #{g.locale} THEN CAT3.ITEMCAT_NM_EN ELSE CAT3.ITEMCAT_NM END FROM ITEMCAT CAT3 WHERE CAT3.TEN_ID = REQ.TEN_ID AND CAT3.ITEMCAT_CD = REQ.ITEMCAT_LVL_3_CD AND CAT3.STS != 'D' ) AS ITEMCAT_LVL_3_NM /* 품목분류3명 */ ,( SELECT CASE WHEN 'en_US' = #{g.locale} THEN CAT4.ITEMCAT_NM_EN ELSE CAT4.ITEMCAT_NM END FROM ITEMCAT CAT4 WHERE CAT4.TEN_ID = REQ.TEN_ID AND CAT4.ITEMCAT_CD = REQ.ITEMCAT_LVL_4_CD AND CAT4.STS != 'D' ) AS ITEMCAT_LVL_4_NM /* 품목분류4명 */ , CASE WHEN 'en_US' = #{g.locale} THEN USR.USR_NM_EN ELSE USR.USR_NM END AS REQR_NM /*요청자*/ FROM ITEM_REG_REQ_EDU REQ LEFT OUTER JOIN USR USR ON USR.TEN_ID = REQ.TEN_ID AND USR.CO_CD = #{g.comp_cd} AND USR.USR_ID = REQ.REQR_ID AND USR.STS != 'D' WHERE REQ.TEN_ID = #{g.tenant} AND REQ.STS != 'D'
AND REQ.ITEMCAT_LVL_1_CD = #{p.itemcat_lvl_1_cd}
AND REQ.ITEMCAT_LVL_2_CD = #{p.itemcat_lvl_2_cd}
AND REQ.ITEMCAT_LVL_3_CD = #{p.itemcat_lvl_3_cd}
AND REQ.ITEMCAT_LVL_4_CD = #{p.itemcat_lvl_4_cd}
AND REQ.REG_DTTM >= #{p.from_date}
AND REQ.REG_DTTM < #{toDatePattern}
AND REQ.REQR_ID = UPPER(#{p.reqr_id})
AND (UPPER(USR.USR_NM) LIKE UPPER(#{reqNmPattern}) OR UPPER(USR.USR_NM_EN) LIKE UPPER(#{reqNmPattern}) )
AND REQ.ITEM_CD IN
#{item_cd}
AND UPPER(REQ.ITEM_CD) LIKE UPPER(#{itemCdPattern})
AND (UPPER(REQ.ITEM_NM) LIKE UPPER(#{itemNmPattern}) OR UPPER(REQ.ITEM_NM_EN) LIKE UPPER(#{itemNmPattern}))
AND REQ.ITEM_REG_REQ_NO IN
#{item_reg_req_no}
AND UPPER(REQ.ITEM_REG_REQ_NO) LIKE UPPER(#{regReqNoPattern})
AND REQ.ITEM_REG_REQ_STS_CCD = #{p.item_reg_req_sts_ccd}
ORDER BY REQ.MOD_DTTM DESC

/* item-req.findInfoItemReq :품목 등록 요청 상세 */ SELECT REQ.TEN_ID /*시스템 아이디 */ , REQ.ITEM_REG_REQ_NO /*등록 요청 번호 */ , REQ.REQ_TYP_CCD /*요청 유형 */ , REQ.ITEM_CD /*품목 코드 */ , REQ.ITEM_NM /*품목 명 */ , REQ.ITEM_NM_EN /*품목 영문 명 */ , REQ.ITEM_SIMWORD /*동의어 */ , REQ.ITEM_SPEC /*규격 */ , REQ.UOM_CCD /*기본 단위 코드 */ , ( SELECT DL.DTLCD_NM FROM DTLCD CD INNER JOIN DTLCD_MULTLANG DL ON CD.TEN_ID = DL.TEN_ID AND CD.CCD = DL.CCD AND CD.DTLCD = DL.DTLCD AND DL.LANG_CCD = #{g.locale} WHERE CD.TEN_ID = #{g.tenant} AND CD.CCD = 'C007' AND CD.USE_YN = 'Y' AND CD.DTLCD = REQ.UOM_CCD ) AS UOM_NM , REQ.ITEMCAT_LVL_1_CD /*분류1 코드 */ , REQ.ITEMCAT_LVL_2_CD /*분류2 코드 */ , REQ.ITEMCAT_LVL_3_CD /*분류3 코드 */ , REQ.ITEMCAT_LVL_4_CD /*분류4 코드 */ , REQ.ITEMCAT_LVL_5_CD /*분류5 코드 */ , ( SELECT CASE WHEN 'en_US' = #{g.locale} THEN CAT1.ITEMCAT_NM_EN ELSE CAT1.ITEMCAT_NM END FROM ITEMCAT CAT1 WHERE CAT1.TEN_ID = REQ.TEN_ID AND CAT1.ITEMCAT_CD = REQ.ITEMCAT_LVL_1_CD ) AS ITEMCAT_LVL_1_NM /* 품목분류1명 */ , ( SELECT CASE WHEN 'en_US' = #{g.locale} THEN CAT2.ITEMCAT_NM_EN ELSE CAT2.ITEMCAT_NM END FROM ITEMCAT CAT2 WHERE CAT2.TEN_ID = REQ.TEN_ID AND CAT2.ITEMCAT_CD = REQ.ITEMCAT_LVL_2_CD ) AS ITEMCAT_LVL_2_NM /* 품목분류2명 */ , ( SELECT CASE WHEN 'en_US' = #{g.locale} THEN CAT3.ITEMCAT_NM_EN ELSE CAT3.ITEMCAT_NM END FROM ITEMCAT CAT3 WHERE CAT3.TEN_ID = REQ.TEN_ID AND CAT3.ITEMCAT_CD = REQ.ITEMCAT_LVL_3_CD ) AS ITEMCAT_LVL_3_NM /* 품목분류3명 */ , ( SELECT CASE WHEN 'en_US' = #{g.locale} THEN CAT4.ITEMCAT_NM_EN ELSE CAT4.ITEMCAT_NM END FROM ITEMCAT CAT4 WHERE CAT4.TEN_ID = REQ.TEN_ID AND CAT4.ITEMCAT_CD = REQ.ITEMCAT_LVL_4_CD ) AS ITEMCAT_LVL_4_NM /* 품목분류4명 */
, REQ.ATHG_UUID /*첨부 번호 */ , REQ.IMG_ATHG_UUID /*이미지 첨부 번호 */
, REQ.MFGR_NM /*제조사 명 */ , REQ.MDL_NO /*모델 번호 */
, REQ.RMK /*비고 */ , REQ.ITEM_REG_REQ_RET_RSN , REQ.ITEM_REG_REQ_STS_CCD /*진행 상태 T:임시저장,P:요청,C:승인,B:반려*/ , REQ.STS , REQ.REGR_ID , REQ.REG_DTTM , REQ.MODR_ID , REQ.MOD_DTTM
, REQ.REQR_ID /*요청자 아이디 */ , ( SELECT CASE WHEN 'en_US' = #{g.locale} THEN USR.USR_NM_EN ELSE USR.USR_NM END FROM USR USR WHERE USR.TEN_ID = REQ.TEN_ID AND USR.CO_CD = #{g.co_cd} AND USR.USR_ID = REQ.REQR_ID AND USR.STS != 'D' ) AS REQR_NM /*요청자*/ , ( SELECT USR.TEL FROM USR USR WHERE USR.TEN_ID = REQ.TEN_ID AND USR.CO_CD = #{g.co_cd} AND USR.USR_ID = REQ.REQR_ID AND USR.STS != 'D' ) AS REQR_PHONE /*요청자*/ , ( SELECT CASE WHEN 'en_US' = #{g.locale} THEN ORG.LOGIC_ORG_NM_EN ELSE ORG.LOGIC_ORG_NM END FROM USR USR LEFT OUTER JOIN LOGIC_ORG ORG /* 조직*/ ON ORG.TEN_ID = USR.TEN_ID AND ORG.LOGIC_ORG_TYP_CCD = 'COMPANY' AND ORG.LOGIC_ORG_CD = USR.CO_CD AND ORG.USE_YN = 'Y' AND ORG.STS != 'D' WHERE USR.TEN_ID = REQ.TEN_ID AND USR.USR_ID = REQ.REQR_ID ) AS REQR_COMP , ( SELECT CASE WHEN 'en_US' = #{g.locale} THEN DEPT.DEPT_NM_EN ELSE DEPT.DEPT_NM END FROM USR USR LEFT OUTER JOIN DEPT DEPT ON DEPT.TEN_ID = USR.TEN_ID AND DEPT.DEPT_CD = USR.DEPT_CD AND DEPT.STS != 'D' LEFT OUTER JOIN LOGIC_ORG_DEPT_MAPG ODM on DEPT.TEN_ID = ODM.TEN_ID AND DEPT.DEPT_CD = ODM.DEPT_CD AND ODM.LOGIC_ORG_TYP_CCD = 'COMPANY' AND ODM.LOGIC_ORG_CD = USR.CO_CD AND ODM.STS != 'D' WHERE USR.TEN_ID = REQ.TEN_ID AND USR.USR_ID = REQ.REQR_ID ) AS REQR_DEPT , REQ.REQ_DTTM , REQ.APVR_ID , ( SELECT CASE WHEN 'en_US' = #{g.locale} THEN USR.USR_NM_EN ELSE USR.USR_NM END FROM USR USR WHERE USR.TEN_ID = REQ.TEN_ID AND USR.CO_CD = #{g.co_cd} AND USR.USR_ID = REQ.APVR_ID AND USR.STS != 'D' ) AS APVR_NM /*승인자명*/ , ( SELECT USR.TEL FROM USR USR WHERE USR.TEN_ID = REQ.TEN_ID AND USR.CO_CD = #{g.co_cd} AND USR.USR_ID = REQ.APVR_ID AND USR.STS != 'D' ) AS APVR_PHONE /*요청자*/ , ( SELECT CASE WHEN 'en_US' = #{g.locale} THEN ORG.LOGIC_ORG_NM_EN ELSE ORG.LOGIC_ORG_NM END FROM USR USR LEFT OUTER JOIN LOGIC_ORG ORG /* 조직*/ ON ORG.TEN_ID = USR.TEN_ID AND ORG.LOGIC_ORG_TYP_CCD = 'COMPANY' AND ORG.LOGIC_ORG_CD = USR.CO_CD AND ORG.USE_YN = 'Y' AND ORG.STS != 'D' WHERE USR.TEN_ID = REQ.TEN_ID AND USR.USR_ID = REQ.APVR_ID ) AS APVR_COMP , ( SELECT CASE WHEN 'en_US' = #{g.locale} THEN DEPT.DEPT_NM_EN ELSE DEPT.DEPT_NM END FROM USR USR LEFT OUTER JOIN DEPT DEPT ON DEPT.TEN_ID = USR.TEN_ID AND DEPT.DEPT_CD = USR.DEPT_CD AND DEPT.STS != 'D' LEFT OUTER JOIN LOGIC_ORG_DEPT_MAPG ODM ON DEPT.TEN_ID = ODM.TEN_ID AND DEPT.DEPT_CD = ODM.DEPT_CD AND ODM.LOGIC_ORG_TYP_CCD = 'COMPANY' AND ODM.LOGIC_ORG_CD = USR.CO_CD AND ODM.STS != 'D' WHERE USR.TEN_ID = REQ.TEN_ID AND USR.USR_ID = REQ.APVR_ID ) AS APVR_DEPT , REQ.APVD_DTTM FROM ITEM_REG_REQ_EDU REQ WHERE REQ.TEN_ID = #{g.tenant} AND REQ.ITEM_REG_REQ_NO = #{p.item_reg_req_no}

mapper.delete:
/* item-req.deleteItemRegReq : 품목 등록 요청 삭제 */
DELETE FROM ITEM_REG_REQ_EDU
WHERE TEN_ID  = #{g.tenant}
AND ITEM_REG_REQ_NO = #{p.item_reg_req_no}

mapper.select:
/* item-req.checkExistedItemRegReq : 결재 요청중이거나 작성중인 데이터가 있는 지 확인 */
SELECT
		CAST(COUNT(ITEM_REG_REQ_NO) AS NUMERIC) AS CNT  FROM ITEM_REG_REQ_EDU
WHERE TEN_ID = #{g.tenant}
AND ITEM_REG_REQ_NO != #{p.item_reg_req_no}
AND ITEM_CD = #{p.item_cd}
AND ITEM_REG_REQ_STS_CCD IN ('CRNG', 'APVL_REQG') /* 작성중, 결재 요청중 */
AND STS != 'D'
